install_packages() {
  # Install required packages
  for package in "${packages[@]}"; do
    if ! $(is_installed "$package"); then
        installer "$package"
    fi
  done

  for package in  "${packages[@]}"; do
    if ! $(is_installed "$package"); then
        echo -e "${error}${package} cannot be installed!\007\n"
        exit 1
    fi
  done
}

welcome() {
    clear
    if $(is_installed lolcat); then
      echo -e "$logo" | lolcat
    else
      echo -e "$logo"
    fi
}


help() {
