${cyan}>${purple} more             ${yellow}More tools from author
${cyan}>${purple} exit             ${yellow}Exit from this program${nc}
"
}

get_list() {
  if [ "$1" == "name" ]; then
    show_name=true
  else
    show_name=false
  fi
  midargs=""
  piped=""
  music_list=()
  if ! $is_recursive; then
    midargs+="-maxdepth 1"
  fi
  if $show_name; then
    format="%f"
  else
    format="%P"
  fi

  if $is_sort; then
    mp3list=$(find $directory $midargs -type f -name "*.mp3" -printf "$format\n" | awk -F '/' '{print $NF "=" $0}' | sort | awk -F "=" '{print $2}')
  else
    mp3list=$(find $directory $midargs -type f -name "*.mp3" -printf "$format\n")
  fi
  replaced=${mp3list// /%%}
  for item in $replaced; do
    music_list+=("$item")
  done
}

get_timeout() {
  timeout_count="$1"
  min=$(expr $timeout_count / 60)
  sec=$(expr $timeout_count % 60)
  if [[ $min -ne 0 ]]; then
    minute=" ${min}m"
  fi
  if [[ $sec -ne 0 ]]; then
    second=" ${sec}s"
  fi
  echo "${minute}${second}"
}

list() {
  get_list name
  if [ -n "$music_list" ]; then
    count=${#music_list[@]}
    for index in $(seq $count); do
      item=${music_list[(($index-1))]}
      music=${item//%%/ }
      if (( index % 2 == 0 )) ; then
        echo -e "${yellow}[$index]${blue} ${music}"
      else
        echo -e "${green}[$index]${purple} ${music}"
      fi
    done
  else
     error "No music in $blue$directory!"
  fi
}

search() {
  term="$1"
  found=0
  get_list name
  if [ -n "$music_list" ]; then
    count=${#music_list[@]}
    for index in $(seq $count); do
      item=${music_list[(($index-1))]}
      music=${item//%%/ }
      if echo "$music" | grep -iEq "$term"; then
        ((found++))
        if (( index % 2 == 0 )) ; then
          echo -e "${yellow}[$index]${blue} ${music}"
        else
          echo -e "${green}[$index]${purple} ${music}"
        fi
      fi
    done
    if [[ $found -eq 0 ]]; then
      error "No music found for $term"
    elif [[ $found -eq 1 ]]; then
      echo
      success "1 match found for $term!"
    else
      echo
      success "$found matches found for $term!"
    fi
  else
     error "No music in $blue$directory!"
  fi
}

swap_dir() {
  dir="$1"
  if echo "$dir" | grep -q "~"; then
    swapped=$(echo "$dir" | sed s+~+$HOME+g)
  fi
  if echo "$dir" | grep -q "$HOME"; then
    swapped=$(echo "$dir" | sed s+$HOME+~+g)
  fi
  if [ -n "$swapped" ]; then
    echo "$swapped"
  else
    echo "$dir"
  fi
}

chdir() {
  dir="$1"
  if echo "$dir" | grep -q "~"; then
    sysdir=$(swap_dir "$dir")
  else
    sysdir="$dir"
  fi
  if [ -d "$sysdir" ]; then
    directory="$sysdir"
    success "Directory set to ${cyan}${dir}${green}!"
  else
    error "$sysdir doesn't exist!"
  fi
}

play() {
  index="$1"
  get_list
  count=${#music_list[@]}
  all=false
  if [ -z "$index" ] && $is_shuffle; then
    index=$(shuf -i 1-${count} -n 1)
  elif [[ $index =~ $num ]] && [[ $index -gt 0 &&  $index -le $count ]]; then # Handles normal playing by index
    printf "" 
  elif  [[ "$index" == "" ]] || [[ "$index" == "all" ]] || [[ "$index" == "a" ]]; then # Handles playing all
    index=0
    all=true
  else
    error "Cannot play $index!"
    return
  fi
  while [[ $repeat_count -ge 0 ]] && [[ $timeout_count -ge 0 ]]; do
    trap "break" 2
    if $all; then
      if $is_shuffle; then
        index=$(shuf -i 1-${count} -n 1)
      else
        ((index++))
      fi
    fi
    item=${music_list[(($index-1))]}
    base=$(basename "$item")
    music=${item//%%/ }
    name=${base//%%/ }
    runtime=$(mediainfo "$directory/$music" | grep Duration | head -1 | awk -F ":" '{print $2}')
    if [ -n "$runtime" ]; then
      min=$(echo "$runtime" | awk '{print $1}')
      sec=$(echo "$runtime" | awk '{print $3}')
      if ! [[ $min =~ $num ]]; then
        min=0
        sec=$(echo "$runtime" | awk '{print $1}')
      fi
      duration=$(expr $min "*" 60 + $sec)
    else
      error "Failed to fetch duration!"
      duration=0
    fi
    if [[ $duration -eq 0 ]] || [[ $duration -gt $timeout_count ]]; then
      success "Playing ${cyan}${name}${green}...."
      mpv "$directory/$music"
    fi
    if [[ $timeout_count -ne 0 ]]; then
      timeout_count=$(expr $timeout_count - $duration)
    fi
    if [[ $repeat_count -ne 0 ]]; then
      ((repeat_count--))
    fi
    if ! $all && ! $is_repeat && [ $repeat_count -eq 0 ]; then
      break
    fi
  done
}


shuffle() {
  if $is_shuffle; then
    is_shuffle=false
    success "Shuffle is turned off!"
  else
    is_shuffle=true
    success "Shuffle is turned on!"
  fi
}

recursive() {
  if $is_recursive; then
    is_recursive=false
    success "Recursive is turned off!"
  else
    is_recursive=true
    success "Recursive is turned on!"
  fi
}

sorter() {
  if $is_sort; then
    is_sort=false
    success "Sort is turned off!"
  else
    is_sort=true
    success "Sort is turned on!"
  fi
}


repeat() {
  count="$1"
  if [[ $count =~ $num ]] && [[ $count -ge 0 ]]; then
    repeat_count=$count
    success "Repeat count is set to $repeat_count!"
  elif [ -z "$count" ] || [[ $count -eq 0 ]]; then
    if $is_repeat; then
      is_repeat=false
      success "Repeat is turned off!"
    else
      is_repeat=true
      success "Repeat is turned on!"
    fi
  else
    error "Invalid repeat count $repeat_count!"
  fi
}

timeout() {
  count="$1"
  if [[ $count =~ $num ]] && [[ $count -ge 0 ]]; then
    timeout_count=$count
    timeout_ms=$(get_timeout $timeout_count)
    success "Timeout is set to${timeout_ms}!"
  else
    error "Invalid timeout $timeout_count!"
  fi
}

reset(){
  [ $is_termux ] && directory="/sdcard/Music"  || directory="$HOME/Music"
  is_repeat=false
  is_shuffle=false
  is_recursive=true
  is_sort=true
  timeout_count=0
  repeat_count=0
}


more() {
  xdg-open https://github.com/KasRoudra/KasRoudra#My-Best-Works &> /dev/null &
}


about() {
  welcome
  echo -e "$red[ToolName]  ${cyan}  :[MusPlayer]
$red[Version]    ${cyan} :[1.0]
$red[Description]${cyan} :[Terminal Music Player]
$red[Author]     ${cyan} :[KasRoudra]
$red[Github]     ${cyan} :[https://github.com/KasRoudra] 
$red[Messenger]  ${cyan} :[https://m.me/KasRoudra]
$red[Email]      ${cyan} :[kasroudrakrd@gmail.com]"
}

menu() {
  while true; do
    trap "break" 2
    printf "$prompt"
    read input
    echo
    cmd=$(echo "$input" | cut -d " " -f1)
    arg=$(echo "$input" | cut -d " " -f2)
    case "$cmd" in 
      ls|list)
        list "$arg"
        ;;
      sr|search)
        search "$arg"
        ;;
      p|play)
        play "$arg"
        ;;
      cd|chdir)
        chdir "$arg"
        ;;
      s|shuffle)
        shuffle "$arg"
        ;;
      r|repeat)
        repeat "$arg"
        ;;
      rc|recursive)
        recursive "$arg"
        ;;
      st|sort)
        sorter "$arg"
        ;;
      t|timeout)
        timeout "$arg"
        ;;
      rs|reset)
        reset
        ;;
      clr|clear)
        welcome
        ;;
      m|more)
        more
        ;;
      a|about)
        about
        ;;
      h|help)
        help "$arg"
        ;;
      q|x|exit)
        handle_interrupt
        ;;
      *)
        error "Invalid input '$input'!"
        ;;
    esac
  done
}

end() {
  info "GoodBye!"
}

usage() {
  code="$1"
  head="$green"
  header="$cyan"
  body="$yellow"
  USAGE="${head}USAGE:
   ${header}musplayer [options]
  
${head}OPTIONS:

   ${header}-l, --list                 ${body}Shows a list of music in directory
   ${header}-sr TERM, --search TERM    ${body}Search in music list
   ${header}-p INDEX, --play INDEX     ${body}Play music by index
   ${header}-d DIRECTORY, --dir DIRECTORY
   ${header}                           ${body}Set music directory
   ${header}-s, --shuffle              ${body}Swap shuffle (Default: $is_shuffle)
   ${header}-r NUMBER, --repeat NUMBER
   ${header}                           ${body}Swap repeat for n times (Default: $is_repeat)
   ${header}-rc, --recursive           ${body}Swap recursive (Default: $is_recursive)
   ${header}-st, --sort                ${body}Swap sorting (Default: $is_sort)
   ${header}-t TIMEOUT, --timeout TIMEOUT
   ${header}                           ${body}Close playing after n seconds
   ${header}-a, --about                ${body}Shows tool information
   ${header}-m, --more                 ${body}A link to github for more script
   ${header}-h, --help                 ${body}Shows this help
   ${header}-v, --version              ${body}Shows script version

    
${head}EXAMPLE:
   ${header}musplayer -v"
   
  ALL="${head}NAME:
   ${header}MusPlayer
  
${head}DESCIPTION:
   ${header}Terminal music player
  
$USAGE
   
${head}COPYRIGHT:
   ${header}KasRoudra (2023)

${head}LICENSE:
   ${header}MIT

${head}VERSION:
   ${header}${version}
"
  if [ $code -eq 0 ]; then
    echo -e "$ALL"
  else 
    echo -e "$USAGE"
  fi
}

main() {
    
  cwd=$(pwd)
  
  # Termux
  if echo "$HOME" | grep -q "termux"; then
    is_termux=true
    directory="/sdcard/Music"
    touch "$directory/.temp" || termux-setup-storage
    rm "$directory/.temp" || termux-setup-storage
  else
    is_termux=false
  fi
  
  if ! [ -d "$directory" ]; then 
    mkdir -p "$directory"
  fi

  # Prevent ^C
  stty -echoctl

  # Detect UserInterrupt
  trap "handle_interrupt" 2
  
  install_packages
  
  while [ "$#" -gt 0 ]; do
    case $1 in
      -l|--list)
        list
        exit 0
        ;;
      -sr|search)
        search $2
        exit 0
        ;;
      -d|--directory)
        chdir $2
        shift
        ;;
       -p|--play)
        play $2
        [ -n $3 ] && shift || exit 0
        ;;
      -h|--help)
        usage 0
        exit 0
        ;;
      -s|--shuffle)
        shuffle
        ;;
      -r|--repeat)
        repeat $2
        shift
        ;;
      -rc|--recursive)
        recursive
        ;;
      -st|--sort)
        sorter
        ;;
      -t|--timeout)
        timeout $2
        shift
        ;;
      -a|--about)
        about 
        exit 0
        ;;
      -m|--more)
        more 
        exit 0
        ;;
      -v|--version)
        info "${cyan}Version: ${green}${version}"
        exit 0
        ;;
      *)
        error "Unknown option $1!"
        usage 1
        exit 1
        ;;
        esac
    shift
  done
  welcome
  menu
  end
}

main $@
